func compareCalculateWithAll(list []int, list1 []int) {
	var times, charges int
	for k := 1; k <= len(list); k++ {
		_, time, charge := LomutoQuiteSelect(list, k)
		//fmt.Println(fmt.Sprintf("当前第%d个小的元素是：%d", k, s))
		times = times + time
		charges = charges + charge
		//fmt.Println()
	}

	fmt.Println(fmt.Sprintf("从第一大元素开始查找总共进行了%d次划分操作,实际进行交换的次数为：%d", times, charges))

	times = 0
	charges = 0
	for k := len(list1); k >= 1; k-- {
		_, time, _ := LomutoQuiteSelect(list1, k)
		//fmt.Println(fmt.Sprintf("当前第%d个小的元素是：%d", k, s))
		times = times + time
		charges = charges + charge
		//fmt.Println()
	}

	fmt.Println(fmt.Sprintf("从第一大元素开始查找总共进行了%d次划分操作,实际进行交换的次数为：%d", times, charges))
}

func compareCalculate(list []int, list1 []int, num int) {
	var times, charges int
	for k := 1; k <= num; k++ {
		_, time, charge := LomutoQuiteSelect(list, k)
		//fmt.Println(fmt.Sprintf("当前第%d个小的元素是：%d", k, s))
		times = times + time
		charges = charges + charge
		//fmt.Println()
	}

	fmt.Println(fmt.Sprintf("从第一大元素开始查找总共进行了%d次划分操作,实际进行交换的次数为：%d", times, charges))

	times = 0
	charges = 0
	for k := num; k >= 1; k-- {
		_, time, _ := LomutoQuiteSelect(list1, k)
		//fmt.Println(fmt.Sprintf("当前第%d个小的元素是：%d", k, s))
		times = times + time
		charges = charges + charge
		//fmt.Println()
	}

	fmt.Println(fmt.Sprintf("从第一大元素开始查找总共进行了%d次划分操作,实际进行交换的次数为：%d", times, charges))
}
